// JavaScript Document


$(document).ready(function(){
	"use strict";
	
	appendmonster();
	
	 $('p').click(function(){
    	click_handler($(this).attr('data-value'));
	 });
	
	 $('#back').click(function(){
    	$('#message').css('visibility', 'hidden');
	 });
	
		$('#little_monster').hide();
	
});


function click_handler(id){
	"use strict";
	
	switch (id){
			
		case '1': $('.monster').css('visibility', 'visible');
							break;
			
		case '2': $('.monster').hide();
							break;
			
		case '3': $('.monster').show();
							break;
			
		case '4': $('.monster').toggle();
							break;
	
		case '5': $('article').slideUp("slow");
							break;
			
		case '6': $('article').slideDown("slow");
							break;
			
		case '7': $('article').slideToggle("slow");
							break;
			
		case '8': $('#little_monster').fadeIn();
							break;
		
		case '9': $('#little_monster').fadeOut();
							break;
		
		case '10': $('article').addClass('sky');
							break;

		case '11': $('#wrapper').before("<p class='dynamic'>Inserting .before #wrapper!</p>");
							break;
			
		case '12': $('#wrapper').after("<p class='dynamic'>Inserting .after wrapper!</p>");
							break;
			
		case '13': $('header').append("<p class='dynamic'>Appending .p to header!</p>");
							break;
			
		case '14': gethtml();
							break;
			
		case '15': getattr();
							break;
			
		case '16': getval();
							break;
			
		case '17': gettext();
							break;
			
		case '18': setdata();
							break;
		
	}
	
	
}

function gethtml(){
	"use strict";
	var htmltext = $('header').html();
	$('#message p').html("Getting .html from header: <br>"+htmltext);
	$('#message').css('visibility', 'visible');
}

function getattr(){
	"use strict";
	var getattr = $('section p:first-child').attr('data-value');
	$('#message p').html("Getting .attr from section p: <br>"+getattr);
	$('#message').css('visibility', 'visible');
}

function getval(){
	"use strict";
	$('#message p').html("Checkbox: <input type='checkbox' id='checkbox' name='checkboxname' value='check1' checked>");
	$('#message p').append("<br> Getting .val for checkbox: " +$('#checkbox').val());
	$('#message').css('visibility', 'visible');
}

function gettext(){
	"use strict";
	var gettext = $('h1').text();
	$('#message p').html("Getting .text from header: <br>"+gettext);
	$('#message').css('visibility', 'visible');
}

function setdata(){
	"use strict";
	$('#message p').html(".data() will store arbitrary data associated with the specified element and/or return the value that was set.");
	$('#message').css('visibility', 'visible');
}

